import React, { Component } from 'react';

import Burger from 'react-css-burger';

class Menu extends Component {
  state = {
    active: false,
  };

  handleMenuActive = (e) => {
    e.preventDefault();
    this.setState({
      active: !this.state.active,
    });
  };

  render() {
    return (
      <div>
 <Burger
        onClick={() =>
          this.setState({
            active: !this.state.active,
          })
        }
        active={this.state.active}
        {...this.props}
      />
      </div>
    );
  }
}

export default Menu;
