import React, { Component } from 'react';

import Burger from 'react-css-burger';

class Menu extends Component {
  state = {
    active: false,
  };

  handleMenuActive = (e) => {
    e.preventDefault();
    this.setState({
      active: !this.state.active,
    });
  };

  render() {
    return (
      <div>
        {' '}
        <Burger
          onClick={() => {
            this.handleMenuActive;
          }}
          active={this.props.menuActive}
          burger="elastic"
          color="black"
          hoverOpacity={0.8}
          scale={1}
        />
      </div>
    );
  }
}

export default Menu;
