import React, { Component, useEffect } from 'react';
import Aos from 'aos';
import 'aos/dist/aos.css';
import './style.scss';

import about from './../../images/about-img.png';

const About = () => {
  useEffect(() => {
    Aos.init({ duration: 2000 });
  }, []);

  return (
    <div className="about" id="About">
      <div className="about-text">
        <div data-aos="fade-down">
          <h3>Sobre</h3>
          <h1>Myrian Katto</h1>
          <p>
            I'm a Japanese-Brazilian Web Developer based in Florianópolis, Brazil. After working
            with graphic design for almost 8 years, I've decided to switch my career to something
            more challenging. I just graduated from an intensive Full Stack Web Development bootcamp
            and now I'm looking forward to putting all this new knowledge in practice combined with
            my design skills. I’m looking for an opportunity in the tech industry where I can
            continue learning, collaborating, creating and developing new great innovative solutions
            for the world. Sou uma Web Developer brasileira com ascendência japonesa vivendo em
            Florianópolis, Brasil. Trabalhei com design gráfico por quase 8 anos, e em 2020 decidi
            transformar minha carreira para algo mais desafiador. Acabei de finalizar um intensivo
            bootcamp de Full Stack Web Development e agora estou em ansiosa para colocar este meu
            novo conhecimento em prática juntamento com meus conhecimentos de design.
          </p>
        </div>
      </div>
      <div className="about-image" data-aos="fade">
        <img src={about} alt="Myrian Katto" />
      </div>
    </div>
  );
};

export default About;
