import React, { Component } from 'react';

import {BrowserRouter as Router, Switch,
  Route,
  Link
} from "react-router-dom";

import Header from './components/Header/Header';
import About from './components/About/About';
import NavBar from './components/NavBar/NavBar';
import NavBarMenu from './components/NavBar/NavBarMenu';
import { BrowserRouter } from 'react-router-dom';
import Tasksy from './components/Projects/Tasksy';
import BooksandTea from './components/Projects/BooksandTea';
import Wasabi from './components/Projects/wasabi';
import Technologies from './components/Technologies/Technologies';
import Experience from './components/Experience/Experience';
import Contacts from './components/Contacts/Contacts';

import './App.scss';

export class App extends Component {
  constructor() {
    super();

    this.state = {
      menuActive: false,
    };

    this.clickHandler = this.clickHandler.bind(this);
  }

  clickHandler() {
    this.setState({ menuActive: !this.state.menuActive });
  }

  render() {
    return (
      <BrowserRouter>
        <div className="App">
          <NavBar onClick={this.clickHandler} menuActive={this.state.menuActive} />
          {this.state.menuActive ? <NavBarMenu onClick={this.clickHandler} menuActive={this.state.menuActive}/> : ''}
          <Header /> 
          <About />
          <Tasksy />
          <BooksandTea />
          <Wasabi />
          <Technologies />
          <Experience />
          <Contacts />
        </div>

        <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/users">
            <Users />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>

      </BrowserRouter>
    );
  }
}

export default App;
