import React from 'react';
import HorizontalTimeline from 'react-horizontal-timeline';

import './style.scss';

const EXAMPLE = [
  {
    statusB: 'Junior Web Developer',
    statusE: 'Freelancer',
  },
  {
    statusB: 'Web Developemnt Bootcamp',
    statusE: 'IronHack Lisbon',
  },
  {
    statusB: 'Senior Graphic Designer',
    statusE: 'Sebrae SP',
  },
  {
    statusB: 'Senior Graphic Designer',
    statusE: 'Inker Agência Cultural',
  },
  {
    statusB: 'Master Degree in Strategic Design',
    statusE: 'IED Rio de Janeiro',
  },
  {
    statusB: 'Graphic Designer',
    statusE: 'BB Básico',
  },
  {
    statusB: 'Junior Graphic Designer',
    statusE: 'Portal Unimed',
  },
  {
    data: '01/01/2020',
    status: 'status',
    statusB: 'Bachelor Degree in Graphic Design',
    statusE: 'UDESC',
  },
];

class Experience extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      curIdx: 0,
      prevIdx: -1,
    };
  }

  //state = { value: 0, previous: 0 };

  render() {
    const { curIdx, prevIdx } = this.state;
    const curStatus = EXAMPLE[curIdx].statusB;
    const prevStatus = prevIdx >= 0 ? EXAMPLE[curIdx].statusE : '';

    return (
      <div className="experience" id="Experience">
        <div className="experience-title">
          <h1>Experience</h1>
          <h2>Work & Study</h2>
        </div>
        {/* Bounding box for the Timeline */}
        <div className="experience-timeline">
          <HorizontalTimeline
            styles={{
              background: '#F8FAFD',
              foreground: '#f35252',
              outline: '#dfdfdf',
            }}
            getLabel={(date) => {
              return date.substring(0, 4);
            }}
            fillingMotion={{ stiffness: 50, damping: 15 }}
            slidingMotion={{ stiffness: 50, damping: 10 }}
            linePadding={100}
            labelWidth={50}
            index={this.state.curIdx}
            indexClick={(index) => {
              const curIdx = this.state.curIdx;
              this.setState({ curIdx: index, prevIdx: curIdx });
            }}
            values={[
              '2020-01-01',
              '2020-01-01',
              '2019-01-01',
              '2018-01-01',
              '2016-01-01',
              '2015-01-01',
              '2013-01-01',
              '2012-01-01',
            ]}
          />
        </div>
        <div className="text-center">
          {/* any arbitrary component can go here */}
          {curStatus} - {prevStatus}
        </div>
      </div>
    );
  }
}

export default Experience;
