import React from 'react';
import Typewriter from 'typewriter-effect';

export default function Header() {
  return (
    <div className="header-text">
      {/* <h2>
        Full Stack Web Developer focused on
        <span><Typewriter
          options={{

            strings: [
              'user experiences.',
              'new technologies.',
              'creative solutions.',
              'attention to details.',
            ],
            autoStart: true,
            loop: true,
            wrapperClassName: 'typewriter-text',

          }}
        /></span>
      </h2> */}
      <h2>
        <Typewriter
          onInit={(typewriter) => {
            typewriter
              .typeString('Full Stack Web Developer focused on <strong>user experiences.<strong>')
              .pauseFor(200)
              .deleteChars(17)
              .typeString('<strong>new technologies.<strong>')
              .wrapperClassName(typewriter-text)
              .pauseFor(200)
              .deleteChars(17)
              .typeString('<strong>creative solutions.<strong>')
              .pauseFor(200)
              .deleteChars(19)
              .typeString('<strong>attention to details.<strong>')
              .start();
          }}
        />
      </h2>
    </div>
  );
}
