import React from 'react';
import './style.scss';
import { Link } from 'react-router-dom';
import books from './../../images/img-books.png';
import github from './../../images/ic-navbar-github.svg';

import { Icon } from 'react-icons-kit';
import { arrows_slim_right } from 'react-icons-kit/linea/arrows_slim_right';

//Icons

import mongodb from './../../images/mongodb.svg';
import express from './../../images/expressjs-icon.svg';
import node from './../../images/nodejs-icon.svg';
import handlebars from './../../images/handlebars-1.svg';
import bootstrap from './../../images/bootstrap-4.svg';

function BooksandTea() {
  return (
    <div className="project-grey-bg">
      <div className="project-image">
        <img style={{ paddingLeft: '4.5em' }} src={books} alt="Myrian Katto" />
      </div>
      <div className="project-content">
        <h1>Books and Tea</h1>
        <p>
          Books and Tea is a platform for book lovers. Users can search for books, add and remove
          them to different bookshelves, rate them and leave a review. It was developed with Google Books API.
        </p>
        <div className="content-btn">
          <Link to="https://github.com/myriankatto/project2" target="_blank">
            <div className="github-btn">
              <img src={github} alt="github" /> <span>Github</span>
            </div>
          </Link>
          <Link to="https://booksandtea.herokuapp.com/" target="_blank">
            <div className="project-btn">
              <span>See Project</span>
              <Icon icon={arrows_slim_right} size={35} />
            </div>
          </Link>
        </div>
        <div className="content-icons">
          <span>Developed with</span>
          <div className="icons">
            <img className="node" src={node} style={{ width: '1em' }} alt="node.js" /> <span>Node.js</span>
            <img className="mongodb" src={mongodb} style={{ width: '3.8em' }} alt="mongodb" /><span >Mongo DB</span>
            <img className="express" src={express} style={{ width: '1.2em' }} alt="express.js" /><span>Express.js</span>
            <img className="handlebars" src={handlebars} style={{ width: '2.2em' }} alt="handlebars" />
            <img className="" src={bootstrap} style={{ width: '1em' }} alt="bootstrap" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default BooksandTea;
