import React, { Component } from 'react';

import Burger from 'react-css-burger';

class Menu extends Component {
  state = {
    active: false,
  };

  render() {
    return (
      <div style={{marginRight: '-10px',  cursor: 'pointer'}}>
        <Burger
          onClick={() =>
            this.props.handleMenuActive
          }
          active={this.state.active}
          {...this.props}
          burger="collapse"
        color="#9a9b9e"
        scale={0.6}

        />
      </div>
    );
  }
}

export default Menu;
