import React from 'react';
import Typewriter from 'typewriter-effect';

export default function Header() {
  return (
    <div className="header-text">
      {/* <h2>
        Full Stack Web Developer focused on
        <span><Typewriter
          options={{

            strings: [
              'user experiences.',
              'new technologies.',
              'creative solutions.',
              'attention to details.',
            ],
            autoStart: true,
            loop: true,
            wrapperClassName: 'typewriter-text',

          }}
        /></span>
      </h2> */}
<h2>
      <Typewriter
  onInit={(typewriter) => {
    typewriter
    .typeString('Full Stack Web Developer focused on <span className>')
      .pauseFor(2500)
      .deleteAll()
      .typeString('Strings can be removed')
      .pauseFor(2500)
      .deleteChars(7)
      .typeString('<strong>altered!<strong>')
      .pauseFor(2500)
      .start();
  }}
/></h2>
    </div>
  );
}
