  const toggleMenu = () => setShowMenu(!showMenu);

  return (
    <div style={{ cursor: 'pointer', marginBottom: '20px', marginRight: '-10px' }}>
      <Burger
        onClick={props.onClick}
        onClick={() => { props.onClick(); func2();}}
        active={props.menuActive}
        burger="collapse"
        color="#9a9b9e"
        scale={0.6}
      />
    </div>
  );
};

export default BurgerMenu;
