import React, { Component, useState, useRef } from 'react';
import {
  animated,
  useTransition,
  useSpring,
  useChain,
  config
} from 'react-spring';

import Burger from 'react-css-burger';
import styled from 'styled-components'

class Menu extends Component {

  render() {
    const Nav = styled(animated.nav)`
  background: pink;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;

  ul {
    margin: 0;
    padding: 0;
  }
`

const Li = styled(animated.li)`
  list-style: none;

  a {
    color: #7d9ba8;
    font-size: 50px;
    font-weight: bold;
    text-transform: uppercase;
    text-decoration: none;
    line-height: 1.3;
  }
`
    const menuItems = [
      { name: 'Home', link: '/' },
      { name: 'About', link: '/about/' },
      { name: 'Contact', link: '/contact/' }
    ]
    const Menu = () => {
      const [showMenu, setShowMenu] = useState(true)
      const toggleMenu = () => setShowMenu(!showMenu)

      // Set refs - required for useChain
      const navRef = useRef()
      const liRef = useRef()

      // Setup animation for nav element
      const springProps = useSpring({
        ref: navRef,
        config: config.default,
        from: { width: '0%' },
        to: { width: showMenu ? '100%' : '0%' }
      })

      // Setup animations for nav items
      const liTransitions = useTransition(
        showMenu ? menuItems : [],
        item => item.name,
        {
          ref: liRef,
          trail: 400 / menuItems.length,
          from: { opacity: 0, transform: 'translateY(20px)' },
          enter: { opacity: 1, transform: 'translateY(0)' },
          leave: { opacity: 0, transform: 'translateY(20px)' }
        }
      )

      // On showMenu, start with nav animationm then nav items
      useChain(showMenu ? [navRef, liRef] : [liRef, navRef], [
        0,
        showMenu ? 0.4 : 0.6
      ])

    }

    return (
      <div style={{ marginRight: '-10px', marginBottom: '20px', cursor: 'pointer' }}>
        <Burger
          onClick={this.props.onClick}
          active={this.props.menuActive}
          burger="collapse"
          color="#9a9b9e"
          scale={0.6}
        /> <Nav className="menu" style={springProps}>
        <ul>
          {liTransitions.map(({ item, key, props }) => (
            <Li key={key} style={props}>
              <a href={item.link}>{item.name}</a>
            </Li>
          ))}
        </ul>
      </Nav>


      </div>
    );
  }
}

export default Menu;
