import React, { Component } from 'react';

import Burger from 'react-css-burger';

class Menu extends Component {
  state = {
    active: false,
  };

  render() {
    return (
      <div style={{}}>
        <Burger
          onClick={() =>
            this.setState({
              active: !this.state.active,
            })
          }
          active={this.state.active}
          {...this.props}
          burger="collapse"
        color="#30333d"
        scale={0.6}
        marginBottom='100px'
        />
      </div>
    );
  }
}

export default Menu;
